function emailValid(email) {
    const regex = /^\S+@\S+$/;
    return email && email.length && email.match(regex);
}

jQuery(document).ready(function ($) {
    
    function createCookie(name, value, days) {
            var expires;
            if (days) {
                var date = new Date();
                date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
                expires = "; expires=" + date.toGMTString();
            }
            else {
                expires = "";
            }
            document.cookie = name + "=" + value + expires + "; path=/";
    }

    if(document.cookie.indexOf('terms') !== -1){
            $(".cookie").css('display', 'none');
    }

    $('.agree-cookie').click(function(){
            createCookie('terms', 1 ,30);
            $(".cookie").addClass('hidden');
    });
    
    if ($('ul.tabs__caption').length > 0) {
        $('ul.tabs__caption').on('click', 'li:not(.active)', function () {
            $(this)
                .addClass('active').siblings().removeClass('active')
                .closest('div.tabs').find('div.tabs__content').removeClass('active').eq($(this).index()).addClass('active');
        });
    }

    $( "#open-modal" ).click(function() {
        $(".modal").css('display', 'block');
    });
    $( ".modal-close" ).click(function() {
        $(".modal").css('display', 'none');
    });

    var delay = 400, timer;
    if ( screen.width > 767 ){
        // console.log(screen.width);
        $(".clickLeft").hover(function () {
            timer = setTimeout(function () {
                $(".banner").addClass("hover-left");
            }, delay);
        }, function () {
            $(".banner").removeClass("hover-left");
            clearTimeout(timer);
        });
        $(".clickRight").hover(function () {
            timer = setTimeout(function () {
                $(".banner").addClass("hover-right");
            }, delay);
        }, function () {
            $(".banner").removeClass("hover-right");
            clearTimeout(timer);
        });
    }


    $(document).ready(function() {
        $('.menu-link').bigSlide({
            'side': 'right',
            'menuWidth': '35%',
        }          
        );
    });
    
});
